 import React, { Component } from 'react'

 
function Form(props) {

   fetchMovies = async () => {
       
        try {
            const url = `http://www.omdbapi.com/?s=Harry&apikey=df23ef2`;
            
            const response = await fetch(url);
            const data = await response.json();


            this.setState({
            movies: data
            })

            console.log(data);
            
        } catch (error) {
            console.log(error);
        }
    }
    return (
             <div>
                <form id="searchForm">
			        <h3>Begin the searching!</h3>
			        <input value={this.props.userInput}placeholder="Search Movies..."></input>
		
                    <button onClick={this.fetchMovies}>Movies</button>
		</form>
             </div>
             
         )
         
     
     
 }
 
 export default Form
