import React, { Component } from 'react'
import Form from './Form';
import MovieList from './MovieList';

export class MovieAPI extends Component {

    
    constructor(props) {
        super(props)
    
        this.state = {
            userInput: '',
            movies: []
        }
    }
        
    fetchMovies = async () => {
       
        try {
            const url = `http://www.omdbapi.com/?s=Harry&apikey=df23ef2`;
            
            const response = await fetch(url);
            const data = await response.json();


            this.setState({
            movies: data
            })

            console.log(data);
            
        } catch (error) {
            console.log(error);
        }
    }

    fetchMovie = async () => {
       
        try {
            const url = `http://www.omdbapi.com/?s=${props.userInput}&apikey=df23ef2`;
            
            const response = await fetch(url);
            const data = await response.json();


            this.setState({
            movies: data
            })

            console.log(data);
            
        } catch (error) {
            console.log(error);
        }
    }

    // handleSearch = (searchMovie) => {
    //     console.log('TodoApp->handleAddTodo(seachMovie)');
    //     console.log(searchMovie);

    // }:

  render() {
      const { movies } = this.state; 
    return (
        <div>
            <h1>Movies</h1>
            <button onClick={this.fetchMovies}>Movies</button>
            
           
            <Form
                // fetchMovies={this.handleSearch}
                // handleSearch={this.handleSearch}
            />

            <MovieList />

        
        </div>
        )
    }
}
export default MovieAPI 






