 import React, { Component } from 'react'

 
 class Form extends React.Component{
   constructor(props){
       super(props);
       this.state = {
           movies: this.props.Search
       }
   }

    handleSearch = (searchMovie) => {
        
        console.log(searchMovie);
    }
   

   render(){
      const { movies } = this.state; 
    return (
             <div>
                <form id="searchForm">
			        <h3>Begin the searching!</h3>
			        <input value={this.userInput}placeholder="Search Movies..."></input>
		
                    <button onClick={movies.handleSearch}>Movies</button>
		</form>
             </div>
             
         )
 }
}
 
 export default Form
