    render(); {
        return (
            <div>
                <h1>Movies</h1>
                <button onClick={this.fetchMovies}>Movies</button>
                <Form
                    // fetchMovies={this.handleSearch}
                    // handleSearch={this.handleSearch}
                />

                <MovieList />

               
            </div>
            )
        }
    }
}
export default MovieAPI 






